"""#<span style="color:#3C8A4E; font-size: 1.5em;">**Paso 5: Generamos un poema**</span>"""

def generar_poema(tema, estilo="verso libre", n_ejemplos=3, temperature=0.9):

    # Hace llamado a construir_prompt con el parámetro de n_ejemplos
    prompt = construir_prompt(tema, estilo, n_ejemplos)

    # Asegúrate de que 'client' esté definido y accesible
    completion = client.chat.completions.create(
        model="meta-llama/Meta-Llama-3-8B-Instruct",
        messages=[
            {"role": "system", "content": "Eres un poeta experto en español."},
            {"role": "user", "content": prompt}
        ],
        max_tokens=300,
        temperature=temperature
    )
    return completion.choices[0].message.content

    # Generamos el poema a partir del prompt
poema = generar_poema("naturaleza", "verso libre")
